import React, {useState, useEffect} from "react"
import TutorialDataService from "../Service/TutorialService"

const Tutorial = props => {
    const initialTutorialState = {
        id : null,
        title : "",
        description: "",
        published: false
    }
    const [currentTutorial,setCurrentTutorial] = useState(initialTutorialState)
    const [message,setMessage] = useState("")

    const getTutorial = id =>{
        TutorialDataService.get(id)
        .then( response =>{
            setCurrentTutorial(response.data);
            console.log(response.data,"getTutorialData")
        })
        .catch(e =>{
            console.log(e)
        })
    }

    useEffect(() => {
        getTutorial(props.match.params.id)
    },[props.match.params.id])

    const handleInputChange = event => {
        const {name, value} = event.target;
        setCurrentTutorial({...currentTutorial, [name]:value})
    }
    const updatePublished = status =>{
        const data ={
            id :currentTutorial.id,
            title: currentTutorial.title,
            description: currentTutorial.description,
            published: status
        }

        TutorialDataService.update(currentTutorial.id,data)
        .then(response => {
            setCurrentTutorial({...currentTutorial, published:status})
            console.log(response.data, "updateData")
        })
        .catch(e => {
            console.lpg(e)
        })
    }

    const updateTutorial = () =>{
        TutorialDataService.update(currentTutorial.id, currentTutorial)
        .then(response => {
            console.log(response.data);
            setMessage("The tutirial was updated successfully!")
        })
        .catch(e => {
            console.log(e)
        })
    }

    const deleteTutorial = () => {
        TutorialDataService.remove(currentTutorial.id)
        .then(response => {
            console.log(response.data, "deleteData")
            props.history.push("/tutorial"); // 해당 정보 삭제 후 callback
        })
        .catch(e =>{
            console.log(e)
        })
    }

    return (
        <div>
            {currentTutorial ? (
                <div className = "edit-form">
                    <h4>Tutorial</h4>
                    <form>
                        <div className="form-group">
                            <label htmlFor="title">Title</label>
                            <input  
                                type="text"
                                className="form-control"
                                id = "title"
                                name = "title"
                                value = {currentTutorial.title}
                                onChange={handleInputChange}/>
                        </div>
                        <div className="form-group">
                            <label htmlFor="description">Description</label>
                            <input  
                                type="text"
                                className="form-control"
                                id = "description"
                                name = "description"
                                value = {currentTutorial.description}
                                onChange={handleInputChange}/>
                        </div>
                        <div className="form-group">
                           <label>
                               <strong>Status: </strong>
                           </label>
                           {currentTutorial.published ? "Published" : "Pending"}
                        </div>
                    </form>
                    {currentTutorial.published ? (
                        <button
                        className = "badge badge-primary mr-2"
                        onClick = { () => updatePublished(false)}>
                            UnPublish
                        </button>
                    ) : (
                        <button
                            className="badge badge-primary mr-2"
                            onClick = { () => updatePublished(true)}>
                            Publish
                        </button>
                    )}
                    <button
                        className="badge badge-primary mr-2"
                        onClick = {deleteTutorial}>
                        Delete
                    </button>
                    <button
                        type="submit"
                        className="badge badge-primary mr-2"
                        onClick = {updateTutorial}>
                        Update
                    </button>
                    <p>{message}</p>
                </div>
            ) : (
                <div>
                    <br/>
                    <p>Please click on a Tutorial...</p>
                </div>
            )}
        </div>
    )

}

export default Tutorial
